import React from 'react';
import axios from 'axios';

class COVID19 extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      datas:[],
    };
    const request = axios.create({
      baseURL: 'https://coronavirus-19-api.herokuapp.com/countries'
    })
    request.get('/japan')
    .then(res => {
      this.setState({
        datas: res.data,
      });
    })
  }

  render() {
    console.log(this.state.datas.country);
    return (
      <div>
        {this.state.datas ?
          (<CasePattern
              active = {this.state.datas.active}
              todayCases = {this.state.datas.todayCases}
              deaths = {this.state.datas.deaths}
              todayDeaths = {this.state.datas.todayDeaths}
              recovered = {this.state.datas.recovered}
            />) : (<p>Loading </p>)}
      </div>
    );
  }
}

class CasePattern extends React.Component {
  render() {
    return (
      <React.Fragment>

      <p>active: {this.props.active}</p>
      <p>todayCases: {this.props.todayCases}</p>
      <p>deaths: {this.props.deaths}</p>
      <p>todayDeaths: {this.props.todayDeaths}</p>
      <p>recovered: {this.props.recovered}</p>
      </React.Fragment>

    );
  }
}

export default COVID19;
