import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {count: 0}
  }

  countUp() {
    this.setState({count: this.state.count + 1});
  }
  countDown() {
    this.setState({count: this.state.count - 1});
  }


  render() {
    return(
      <div>
        <p>{this.state.count}</p>
        <button onClick= {() => {this.countUp()}}>CuontUp</button>
        <button onClick= {() => {this.countDown()}}>CuontDown</button>
      </div>
    );
  }
}

export default App;
