import React from 'react';
import axios from 'axios';

class COVID19 extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      datas:[],
      loading: true,
    };
    const request = axios.create({
      baseURL: 'https://coronavirus-19-api.herokuapp.com/countries'
    })
    request.get('/japan')
    .then(res => {
      this.setState({
        datas: res.data,
      });
    })
  }

  render() {
    if(this.state.daas !== null) {
      this.setState({
        loading: false,
      })
    }
    return (
      <div>
      {
        (() => {
          if (this.state.loading) {
            return (
              <p>now Loading</p>
            )
          } else {
            return (
              <p>Country: {this.state.datas.country}</p>
            );
          }
        })()
      }


      </div>
    );
  }
}

export default COVID19;
