import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {name: "hoge"}
  }
  handleClick(name) {
    this.setState({name: name});
  }
  render() {
    return(
      <div>
        <p>{this.state.name}</p>
        <button onClick= {() => {this.handleClick("ねこ")}}>ねこ</button>
        <button onClick= {() => {this.setState({name: "ひつじ"})}}>ひつじ</button>
      </div>
    );
  }
}

export default App;
