import React from 'react';
import axios from 'axios';

import Container from '@material-ui/core/Container';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import CountUp from 'react-countup';

class World extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      datas: [],
    }

    const request = axios.create({
      baseURL: 'https://coronavirus-19-api.herokuapp.com'
    })
    request.get('/all')
    .then(res => {
      console.log(res.data);
      this.setState({
        datas: res.data,
      });
    })
    console.log(this.state.datas)
  }

  render() {
    return (
      <React.Fragment>
        {this.state.datas ?
        (<WorldCases
            cases={this.state.datas.cases}
            deaths={this.state.datas.deaths}
            recovered={this.state.datas.recovered}
          />) : (<p>Loading</p>)}
      </React.Fragment>
    );
  }
}


function WorldCases(props) {
  return (
    <React.Fragment>
      <div className="topWrapper">
        <h2>累計感染者数</h2>
        {props.cases ? (
          <CountUp
            start={0}
            end={props.cases}
            delay={0}
          >
            {({ countUpRef }) => (
              <div className="activeCounter">
                <span ref={countUpRef} /><p>名</p>
              </div>
            )}
            </CountUp>
        ) : (<p>集計中</p>)}

      </div>

      <div className="countryName">
        <h1>World</h1>
      </div>

      <Container className="flexContainer">
      <Grid container spacing={3}>
        <Grid item xs={6}>
          <Paper className="caseCard">
              <h2>累計死亡者数</h2>
              <p><span>{props.deaths}</span>名</p>
          </Paper>
        </Grid>

        <Grid item xs={6}>
          <Paper className="caseCard">
              <h2>累計回復者数</h2>
              <p><span>{props.recovered}</span>名</p>
          </Paper>
        </Grid>

      </Grid>

    </Container>

    </React.Fragment>
  );
}

export default World;
