import React from 'react';
import axios from 'axios';

class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      datas: [],
    }
    const request = axios.create({
      baseURL: 'https://coronavirus-19-api.herokuapp.com'
    })
    const country = this.props.country.replace('<button>', '');
    const countryName = country.replace('</button>', '');
    console.log(countryName);
    request.get(`/${countryName}`)
    .then(res => {
      this.setState({
        datas: res.data,
      });
    })
  }

  render() {
    return (
      <React.Fragment>
        {this.state.datas.map(info => (
          <button onClick={this.props.changeCountry}>{info.country}</button>
        ))}
      </React.Fragment>
    );
  }
}


export default Search;
